window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());

gtag('config', 'G-5G36B723DC');

 // sport disponse
 function toggleInput() {
     checkbox = document.getElementById("dispenserCheckbox");
     input = document.getElementById("sp");

    if (checkbox.checked) {
        input.setAttribute("disabled", "disabled");
    } else {
        input.removeAttribute("disabled");
    }
}
// l'action par défaut ne devrait pas être exécutée comme elle l'est normalement
document.getElementById("myForm").onsubmit = function (e) {
    e.preventDefault();
    calcul_cntrl()
};
//Session principale
function calcul() {
    //Récupérer les notes dans des variables
    var math = parseFloat(document.getElementById("ma").value),
        physique = parseFloat(document.getElementById("phy").value),
        science = parseFloat(document.getElementById("sc").value),
        français = parseFloat(document.getElementById("fr").value),
        philo = parseFloat(document.getElementById("ph").value),
        arabe = parseFloat(document.getElementById("ar").value),
        sport = parseFloat(document.getElementById("sp").value),
        option = parseFloat(document.getElementById("op").value),
        anglais = parseFloat(document.getElementById("an").value),
        info = parseFloat(document.getElementById("in").value),
        checkbox = document.getElementById("dispenserCheckbox"),
        som_notes, som_coef, moy, fg, score7, fb, fs;
    if (option > 10)
        option = eval((option - 10))
    else
        option = 0

    if (checkbox.checked) {
        sport = 10;
    }
    //calculer la moyenne du session principale 
    som_notes = eval((math * 4) + (physique * 4) + science + français + philo + arabe + sport + option + anglais + info);
    som_coef = 15;
    moy = eval(som_notes / som_coef);
    //calculer le score
    fb = eval(moy * 4);
    fs = eval((2 * math) + (1.5 * physique) + (0.5 * science) + anglais + français);
    fg = eval(fb + fs);
    scor7 = eval(fg + ((fg * 7) / 100));
    //affichage des resultas
    document.getElementById("moy").value = "MOYENNE : " + moy.toFixed(2);
    document.getElementById("score").value = "SCORE : " + fg.toFixed(2);
    document.getElementById("score7").value = "SCORE AVEC 7% : " + scor7.toFixed(2);

}
// l'action par défaut ne devrait pas être exécutée comme elle l'est normalement
document.getElementById("myForm2").onsubmit = function (e) {
    e.preventDefault();
    calcul()
};
//Session controle
function calcul_cntrl() {
    //Récupérer les notes dans des variables
    var math1 = parseFloat(document.getElementById("ma1").value),
        physique1 = parseFloat(document.getElementById("phy1").value),
        science1 = parseFloat(document.getElementById("sc1").value),
        français1 = parseFloat(document.getElementById("fr1").value),
        arabe1 = parseFloat(document.getElementById("ar1").value),
        anglais1 = parseFloat(document.getElementById("an1").value),
        math2 = parseFloat(document.getElementById("ma2").value),
        physique2 = parseFloat(document.getElementById("phy2").value),
        science2 = parseFloat(document.getElementById("sc2").value),
        français2 = parseFloat(document.getElementById("fr2").value),
        arabe2 = parseFloat(document.getElementById("ar2").value),
        anglais2 = parseFloat(document.getElementById("an2").value),
        philo_f = parseFloat(document.getElementById("ph_f").value),
        sport_f = parseFloat(document.getElementById("sp_f").value),
        option_f = parseFloat(document.getElementById("op_f").value),
        info_f = parseFloat(document.getElementById("in_f").value),
        math_f, physique_f, science_f, français_f, arabe_f, anglais_f,
        math_s, physique_s, science_s, français_s, arabe_s, anglais_s,
        som_notes2, som_notes1, som_coef, moy, moy1, moy2, fg, score7, fb, fs;
    //obtenir la meilleure note entre session principale et session controle

    math_f = Math.max(math1, math2);

    physique_f = Math.max(physique1, physique2);

    science_f = Math.max(science1, science2);

    français_f = Math.max(français1, français2);

    arabe_f = Math.max(arabe1, arabe2);

    anglais_f = Math.max(anglais1, anglais2);

    if (option_f > 10)
        option_f = eval((option_f - 10));
    else
        option_f = 0;
    //calculer la moyenne du session principale    
    som_notes1 = eval((math1 * 4) + (physique1 * 4) + science1 + français1 + philo_f + arabe1 + sport_f + option_f + anglais1 + info_f);
    som_coef = 15;
    moy1 = eval(som_notes1 / som_coef);
    //control de saisie 
    if (moy1 < 7){
        swal ( "Oops verifier vos notes" ,  "La moyenne de vos notes de principale est moins de 7" ,  "error" )
        return 0;
    }
    if (moy1 >= 10){
        swal ( "Oops verifier vos notes" ,  "La moyenne de vos notes de principale est plus de 10" ,  "error" )
        return 0;
    }
    //calculer la moyenne du session controle 
    som_notes2 = eval((math_f * 4) + (physique_f * 4) + science_f + français_f + philo_f + arabe_f + sport_f + option_f + anglais_f + info_f);
    som_coef = 15;
    moy2 = eval(som_notes2 / som_coef);
    //moyenne generale
    moy = eval(((moy1 * 2) + moy2) / 3);
    //les notes pour calculer le score
    math_s = eval(((math1 * 2) + math2) / 3);
    physique_s = eval(((physique1 * 2) + physique2) / 3);
    science_s = eval(((science1 * 2) + science2) / 3);
    français_s = eval(((français1 * 2) + français2) / 3);
    arabe_s = eval(((arabe1 * 2) + arabe2) / 3);
    anglais_s = eval(((anglais1 * 2) + anglais2) / 3);
    //calculer le score
    fb = eval(moy * 4);
    fs = eval((2 * math_s) + (1.5 * physique_s) + (0.5 * science_s) + anglais_s + français_s);
    fg = eval(fb + fs);
    scor7 = eval(fg + ((fg * 7) / 100));
    //affichage des resultas
    document.getElementById("moy2").value = "MOYENNE : " + moy2.toFixed(2);
    document.getElementById("score2").value = "SCORE : " + fg.toFixed(2);
    document.getElementById("score7_2").value = "SCORE AVEC 7% : " + scor7.toFixed(2);

}
//loading function
$(window).on("load", function () {
    $("#preloader #loader").fadeOut(2000,
        function () {
            $(this).parent().fadeOut(1000,
                function () {
                    $(this).remove();
                });
        });
});

